package main

import (
	"github/ecommerce/internal/adpter/http/handler"
	"github/ecommerce/internal/domain/product"
	"github/ecommerce/internal/usecase"
	"net/http"
)

type Product product.Product

var products []Product

func main() {
	mux := http.NewServeMux()
	http.RegisterRoutes(mux, productHandler)
	http.ListenAndServe(":8080", mux)
}
func init() {
	product1 := Product{
		Id:     1,
		Name:   "Mango",
		Type:   "Fruits",
		Price:  70,
		ImgUrl: "data:image/jpeg;base64,",
	}
	product2 := Product{
		Id:     2,
		Name:   "mause",
		Type:   "electronics",
		Price:  100,
		ImgUrl: "",
	}
	product3 := Product{
		Id:     3,
		Name:   "Sharee",
		Type:   "Shopping",
		Price:  999,
		ImgUrl: "",
	}
	products := append(products, product1)
	products := append(products, product2)
	products := append(products, product3)

	service := usecase.NewProductService(products)
	productHandler := handler.NewProductHandler(service)

}
